import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const AddProduct = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [products, setProducts] = useState(location.state?.products || []);
  const [formData, setFormData] = useState({ name: "", price: "" });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price) {
      setError("All fields are required.");
      return;
    }

    const newProduct = {
      id: products.length + 1,
      name: formData.name,
      price: formData.price,
    };

    console.log("New product added:", newProduct); // Replace with an API call if required
    setProducts([...products, newProduct]); // Update product list (use this with state or API)
    navigate("/products"); // Redirect back to product list
  };

  return (
    <div className="add-product">
      <h1>Add New Product</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Product Price:</label>
          <input
            type="text"
            name="price"
            value={formData.price}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default AddProduct;
