import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditProduct = ({ products, updateProduct }) => {
  const { productId } = useParams(); // Retrieve productId from the URL
  const navigate = useNavigate();

  // Find the product by ID
  const product = products.find((p) => p.id === parseInt(productId, 10));
  const [formData, setFormData] = useState(product || {});
  const [error, setError] = useState("");

  if (!product) {
    navigate("/products"); // Redirect if product is not found
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({ ...formData, image: URL.createObjectURL(file) });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price || !formData.image) {
      setError("All fields are required.");
      return;
    }

    updateProduct(formData); // Update product in the list
    navigate("/products"); // Redirect back to the product list
  };

  return (
    <div className="edit-product">
      <h1>Edit Product</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Product Price:</label>
          <input
            type="text"
            name="price"
            value={formData.price}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Product Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} />
          {formData.image && (
            <img src={formData.image} alt="Preview" className="preview-image" />
          )}
        </div>
        <button type="submit" className="btn">
          Update Product
        </button>
      </form>
    </div>
  );
};

export default EditProduct;
