import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const EditProduct = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  const updateProduct = state?.updateProduct; // Retrieve the updateProduct function
  const product = state?.product; // Retrieve the product to edit

  const [formData, setFormData] = useState(product || {});
  const [error, setError] = useState("");

  if (!updateProduct || !product) {
    navigate("/products"); // Redirect if data is missing
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({ ...formData, image: URL.createObjectURL(file) });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price || !formData.image) {
      setError("All fields, including a picture, are required.");
      return;
    }

    updateProduct(formData); // Update the product using the function
    navigate("/products"); // Redirect back to the product list
  };

  return (
    <div className="edit-product">
      <h1>Edit Product</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Product Price:</label>
          <input
            type="text"
            name="price"
            value={formData.price}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Product Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} />
          {formData.image && (
            <img src={formData.image} alt="Preview" className="preview-image" />
          )}
        </div>
        <button type="submit" className="btn">
          Update Product
        </button>
      </form>
    </div>
  );
};

export default EditProduct;
