import React from "react";
import { useLocation } from "react-router-dom";

// Import images
import img1 from "../assets/images/img1.png";
import img2 from "../assets/images/img2.png";
import img3 from "../assets/images/img3.png";
import img4 from "../assets/images/img4.png";
import img5 from "../assets/images/img5.png";
import img6 from "../assets/images/img6.png";

const ProductList = () => {
  // Images array (can also be passed from the Carousel)
  const images = [
    { id: 1, src: img1, name: "Rose Bouquet", price: "$25" },
    { id: 2, src: img2, name: "Tulip Arrangement", price: "$30" },
    { id: 3, src: img3, name: "Lily Basket", price: "$20" },
    { id: 4, src: img4, name: "Orchid Set", price: "$35" },
    { id: 5, src: img5, name: "Daisy Bundle", price: "$15" },
    { id: 6, src: img6, name: "Sunflower Pack", price: "$40" },
  ];

  // Optional: Check if a specific product was selected
  const location = useLocation();
  const selectedProduct = location.state?.selectedProduct;

  return (
    <div className="product-list">
      <h1>Our Products</h1>
      <div className="products">
        {images.map((product) => (
          <div className="product-item" key={product.id}>
            <img src={product.src} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
          </div>
        ))}
      </div>

      {/* Highlight selected product if provided */}
      {selectedProduct && (
        <div className="selected-product">
          <h2>Selected Product</h2>
          <img
            src={selectedProduct.image}
            alt={`Selected ${selectedProduct.name}`}
          />
          <h3>{selectedProduct.name}</h3>
          <p>{selectedProduct.price}</p>
        </div>
      )}
    </div>
  );
};

export default ProductList;
