import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const EditProduct = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [formData, setFormData] = useState(location.state?.product || {});
  const [error, setError] = useState("");

  if (!formData || !formData.id) {
    navigate("/products"); // Redirect to product list if no product is selected
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price) {
      setError("All fields are required.");
      return;
    }

    console.log("Product updated:", formData); // Replace with an API call if required
    navigate("/products"); // Redirect back to product list
  };

  return (
    <div className="edit-product">
      <h1>Edit Product</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Product Price:</label>
          <input
            type="text"
            name="price"
            value={formData.price}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Update Product</button>
      </form>
    </div>
  );
};

export default EditProduct;
