import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const RecordList = () => {
  const navigate = useNavigate();

  // Example records
  const [records, setRecords] = useState([
    { id: 1, name: "Rose Bouquet", price: "$25" },
    { id: 2, name: "Tulip Arrangement", price: "$30" },
  ]);

  const goToAdd = () => {
    navigate("/add");
  };

  const goToEdit = (record) => {
    navigate("/edit", { state: { record } });
  };

  return (
    <div className="record-list">
      <h1>Manage Records</h1>
      <button onClick={goToAdd}>Add New Record</button>
      <div className="records">
        {records.map((record) => (
          <div key={record.id} className="record-item">
            <h3>{record.name}</h3>
            <p>{record.price}</p>
            <button onClick={() => goToEdit(record)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecordList;
