import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ProductList = () => {
  const navigate = useNavigate();

  // Sample initial products
  const [products, setProducts] = useState([
    { id: 1, name: "Rose Bouquet", price: "$25" },
    { id: 2, name: "Tulip Arrangement", price: "$30" },
    { id: 3, name: "Lily Basket", price: "$20" },
  ]);

  const goToAddProduct = () => {
    navigate("/products/add", { state: { products } }); // Pass current products to add functionality
  };

  const goToEditProduct = (product) => {
    navigate("/products/edit", { state: { product } }); // Pass the selected product for editing
  };

  return (
    <div className="product-list">
      <h1>Product Management</h1>
      <button onClick={goToAddProduct}>Add New Product</button>
      <div className="products">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <h3>{product.name}</h3>
            <p>{product.price}</p>
            <button onClick={() => goToEditProduct(product)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
