import React from "react";
import { useNavigate } from "react-router-dom";
import { useProducts } from "../context/ProductContext";
import "../assets/css/productListStyles.css";

const ProductList = () => {
  const navigate = useNavigate();
  const { products } = useProducts(); // Get products from context

  const goToAddProduct = () => {
    navigate("/products/add");
  };

  const goToEditProduct = (product) => {
    navigate("/products/edit", { state: { product } });
  };

  return (
    <div className="product-list">
      <h1>Product Management</h1>
      <button onClick={goToAddProduct} className="btn">
        Add New Product
      </button>
      <div className="products">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
            <button onClick={() => goToEditProduct(product)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
