import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../assets/css/productListStyles.css";

const EditProduct = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  // Extract product and updateProduct from state
  const updateProduct = state?.updateProduct;
  const initialProduct = state?.product;

  // Initialize form data with product details
  const [formData, setFormData] = useState(
    initialProduct || { name: "", price: "", image: null }
  );
  const [error, setError] = useState("");

  // Handle missing state gracefully
  useEffect(() => {
    if (!initialProduct) {
      setError("No product data found to edit.");
      const timer = setTimeout(() => navigate("/products"), 2000);
      return () => clearTimeout(timer);
    }
  }, [initialProduct, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({ ...formData, image: URL.createObjectURL(file) });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price || !formData.image) {
      setError("All fields, including a picture, are required.");
      return;
    }

    updateProduct(formData); // Update the product in the list
    navigate("/products"); // Redirect back to the product list
  };

  const handleCancel = () => {
    navigate("/products"); // Redirect back to the product list without saving
  };

  return (
    <div className="edit-product">
      <h1>Edit Product</h1>
      {error && <p className="error">{error}</p>}
      {initialProduct && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Product Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter product name"
            />
          </div>
          <div className="form-group">
            <label>Product Price:</label>
            <input
              type="text"
              name="price"
              value={formData.price}
              onChange={handleChange}
              placeholder="Enter product price"
            />
          </div>
          <div className="form-group">
            <label>Product Image:</label>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            {formData.image && (
              <img
                src={formData.image}
                alt="Preview"
                className="preview-image"
              />
            )}
          </div>
          <button type="submit" className="btn">
            Update Product
          </button>
          <button
            type="button"
            className="btn cancel-btn"
            onClick={handleCancel}
          >
            Cancel
          </button>
        </form>
      )}
    </div>
  );
};

export default EditProduct;
